import React from "react";
import Input from "../../components/Input";
import styled from "styled-components";
import Button from "../../components/Button";

const SignInPresenter = ({ createRoom, isVisible }) => {
  return (
    <Container>
      <Image src="/asset/image/person.jpeg" />
      <Text>우리 지금 만나</Text>
      <Input hint={"방 이름을 입력해주세요!"} />
      <Button onClick={createRoom}>방 만들기</Button>
      {isVisible ? (
        <Modal>
          <smallText> 방 id: {"1"}</smallText>
        </Modal>
      ) : null}
    </Container>
  );
};

const Modal = styled.div`
  width: 310px;
  height: 172px;
  background-color: #efefef;
  border-radius: 20px;
  justify-content: center;
  align-items: center;
  display: flex;
  flex-direction: column;
  position: absolute;
`;

const smallText = styled.p`
  margin: 0;
  font-size: 16px;
  font-weight: bold;
  color: ${(props) => props.color || "black"};
`;

const Container = styled.div`
  justify-content: center;
  align-items: center;
  display: flex;
  flex-direction: column;
  opacity: 0.5;
`;

const Text = styled.p`
  font-size: 50px;
  font-weight: bold;
  color: white;
  margin: 0;
  margin-bottom: 36px;
`;

const Image = styled.img`
  width: 200px;
  height: 200px;
  border-radius: 30px;
  margin-bottom: 18px;
  margin-top: 30px;
`;

export default SignInPresenter;
